/*
** Styles containing Wikilogic from
** $:/themes/telmiger/bricks/wikilogic-42-77
** inspired by TW vanilla
** """ 
*/

html {
	font-size: {{$:/themes/telmiger/bricks/metrics/fontsize}};
}

/*
** no-sidebar rules – mostly from .../001-page.css
*/

@media (<<--breakpoint-medium>>) {

<<if-no-sidebar "

	body .tc-tiddler-view-frame, body .tc-tiddler-frame.tc-tiddler-edit-frame {
		margin-right: auto;
	}

	body .tc-tiddler-frame.tc-tiddler-edit-frame {
		margin-right: 2rem;
	}

">>
}


@media (<<--breakpoint-large>>) {

<<if-no-sidebar "

	body .tc-story-river {
		margin-right: auto;
		padding-right: 2rem;
	}

	body .tc-tiddler-frame {
		margin-right: auto;
	}

">>
}


/*
** from .../components/tiddler.css
*/

<$reveal state="$:/themes/tiddlywiki/vanilla/options/stickytitles" type="match" text="yes">
.tc-tiddler-title {
	position: -webkit-sticky;
	position: -moz-sticky;
	position: -o-sticky;
	position: -ms-sticky;
	position: sticky;
	top: 0px;
	margin-right: 0rem;
	margin-left: -1rem;
	padding-right: 4rem;
	padding-left: 1rem;
	min-width: calc(100% + 2rem);
	z-index: 300;
	background: <<colour tiddler-background>>;
	border-bottom: 1px solid <<colour tab-divider>>;
}

@media (<<--breakpoint-not-small>>) {
	.tc-tiddler-title {
		margin-right: 0rem;
		margin-left: -1rem;
		padding-right: 2rem;
		padding-left: 1rem;
		min-width: calc(100% + 2rem);
	}
}

@media (<<--breakpoint-medium>>) {
	.tc-tiddler-title {
		padding-right: 1rem;
	}
}
</$reveal>


/*
** from .../edit/tiddler.css
*/

textarea.tc-edit-texteditor {
	font-family: {{$:/themes/tiddlywiki/vanilla/settings/editorfontfamily}};
}

<<if-editor-height-fixed then:"""

body .tc-tiddler-preview-preview {
	overflow-y: scroll;
	height: {{$:/config/TextEditor/EditorHeight/Height}};
}

""">>


/*
** Navigator Plugin – Dynamic Elements
** (c) Thomas Elmiger
*/


#navigator-main {
   background-color: {{$:/themes/telmiger/navigator/settings##colour-main-nav}};
}

#navigator-main button {
   color: <<colour sidebar-tab-foreground>>;
}

#navigator-main button.te-big-new-btn {
   background-color: {{$:/themes/telmiger/navigator/settings##colour-new-button}};
}


/* Show big New button? */

<$reveal type="match" stateTitle="$:/themes/telmiger/navigator/settings" stateIndex="show-new-button" text="no">

.tc-page-container .tc-topbar-left button.te-big-new-btn {
   display: none;
}

</$reveal>

/* Show keyboard hints? */

<$reveal type="match" stateTitle="$:/themes/telmiger/navigator/settings" stateIndex="show-kbd-hints" text="no">

#navigator-main .shortcut-code {
   display: none;
}

</$reveal>


